mapper.update:
update completion_production set
batch = #{batch},
note = #{note}
where id = #{id};

mapper.select:
select * from completion_production

    production_plan = #{productionPlan}
    and start_time = #{startTime}
    and end_time = #{endTime}
    and product like #{product}
    and batch = #{batch}
    and status = #{status}
